import Contact from './components/Contact.js';
import Home from './components/Home.js';
import About from './components/About.js';
import {useState} from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import * as React from "react";
import * as ReactDOM from "react-dom/client";


function App(){
  const [homeVisits, setHomeVisits] = useState(0);

  return <div>
   <nav>
      <a href="./components/Home.js"><Home onClick={()=>{setHomeVisits(homeVisits+1)}}/></a> |
      <a href="/components/About.js"><About  homeVisits={homeVisits}/></a> |
      <a href="/components/Contact.js"><Contact homeVisits={homeVisits}/></a> |
    </nav>
  </div>
}


export default App;
