import Contact from './components/Contact.js';
import Home from './components/Home.js';
import About from './components/About.js';
import {useState} from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import * as React from "react";
import * as ReactDOM from "react-dom/client";


function App() {
  const [homeVisits, setHomeVisits] = useState(0);

  const increaseHomeVisits = () => {
    setHomeVisits(homeVisits + 1);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/contact">Contact</Link>
            </li>
          </ul>
        </nav>

        <Route path="/" exact render={() => <Home increaseHomeVisits={increaseHomeVisits} />} />
        <Route path="/about" render={() => <About homeVisits={homeVisits} />} />
        <Route path="/contact" render={() => <Contact homeVisits={homeVisits} />} />
      </div>
    </Router>
  );
}

export default App;
